mapper.select:
select u.id, u.account, u.name, u.avatar, u.sex, u.birthday, u.email, u.phone, u.dept_id, d.name as deptName, u.status, u.note, u.create_time, u.create_user, u.update_time, u.update_user from sys_user as u left join sys_dept as d on u.dept_id = d.id where u.status != 3
and ( u.account like concat('%', #{keyword}, '%') or u.name like concat('%', #{keyword}, '%') )
and u.dept_id = #{deptId}
and u.status = #{status}
order by u.id

mapper.insert:
insert into sys_user_role (
    user_id,
    role_id
) values

    (
        #{userId},
        #{roleId}
    )

mapper.update:
update sys_user
set status = 3
where id = #{id}
